var constants = {};
function load_SFIdata(name, value){
	constants[name] = value;
}
function getJsonP(name){
	document.write('<script src="https://www.benoldinggames.co.uk/sfi/gamedata/files/'+name+'.jsonp"></script>');
	document.write('<script>if(!("'+name+'" in constants)){document.write(\'<script src="backupData/'+name+'.jsonp"><\\/script>\');}</script>');
	//if(!(name in constants)){
	//	document.write('<script src="backupData/'+name+'.jsonp"></script>');
	//}
}

